import { findDOMNode } from 'react-dom';
import {
    ConnectDragSource,
    ConnectDropTarget,
    DragSource,
    DragSourceConnector,
    DragSourceMonitor,
    DropTarget,
    DropTargetConnector,
    DropTargetMonitor,
    XYCoord
} from 'react-dnd'
import './boxItem.scss'

const source = {
    beginDrag(props) {
        return {
            ...props
        }
    },
    isDragging(props, monitor) {
        return props.id === monitor.getItem().id
    }
}


const target = {
    canDrop(props, monitor) {
        return true
    },
    drop(props, monitor, component) {
        //const { id: draggedId } = monitor.getItem();
        //const { id: dropId } = props;

        const before = findDOMNode(monitor.getItem()).getBoundingClientRect();
        const componentRect = findDOMNode(component).getBoundingClientRect();
        console.log(componentRect)

    }

}


const TYPE = 'ITEM'

class BoxCmp extends React.Component{
    render() {
        const { connectDropTarget, connectDragSource } = this.props;
        return connectDropTarget(
                connectDragSource(
                    <div className='box-feature' />
                )
            )
    }
}
const Box = DropTarget(TYPE, target, (connect) => ({
    connectDropTarget: connect.dropTarget()
}))(DragSource(TYPE, source, (connect, monitor) => ({
    connectDragSource: connect.dragSource(),
    isDragging: monitor.isDragging()
}))(BoxCmp))

export default Box
