/*
 * @Author: zengjian 
 * @Date: 2018-12-10 19:52:51 
 * @Last Modified by: yuanxy
 * @Last Modified time: 2018-12-16 17:54:57
 */
import React from 'react'
import './index.scss'
import connect from '../../redux/connect'
import { DragDropContext } from 'react-dnd'
import HTML5Backend from 'react-dnd-html5-backend'


import FormItem from '../features/boxItem'

class CanvasCmp extends React.Component{
    static defaultProps = {
        zoom:70
    }

    move=(x,y)=>{
        this.props.dispatch({type:'move',payload:{
            x:x,
            y:y
        }})
    }

    render(){
        const {x,y}=this.props

        const percent = this.props.zoom/100
        return <div className='cp-canvas' style={{width:800*percent,height:600*percent}}>
            <FormItem  move={this.move} x={x} y={y}/>
        </div>
    }
}

 const canvas = DragDropContext(HTML5Backend)() 
 export default connect(state=>state.component)(canvas)
