/*
 * @Author: yuanxy 
 * @Date: 2018-12-16 20:38:52 
 * @Last Modified by: yuanxy
 * @Last Modified time: 2018-12-17 00:12:16
 */
import React from 'react'
import {findDOMNode} from 'react-dom'
import {
    DragSource,

} from 'react-dnd'
import './itemBox.scss'

const source = {
    beginDrag(props) {
        return props
    },
    canDrag(props) {
        if (props.id !== props.current) {
            return false
        }
        return true
    }
}

class BoxCmp extends React.Component {
    constructor(props){
        super(props)

    }
    state = {
        type: ''
    }
    componentDidMount() {
        this.mouseUp()
    }
    mouseUp = () => {
        document.addEventListener('mouseup', () => {
            document.removeEventListener('mousemove', this.computeSize);
        });
    };
    onClickHandler = (e, id) => {
        e.stopPropagation()
        this.props.selectItem(id)
    }


    updateSize = (e, type) => {
        e.preventDefault();
        e.stopPropagation();
        this.setState({
            type: type
        })
        document.addEventListener('mousemove', this.computeSize);
    }

    computeSize = (e) => {
        const { type } = this.state
        const { updateSize,id,width,height,x } = this.props
        const { offsetX, offsetY,layerX } = e

         const {x:xx}=findDOMNode(this.ref).getBoundingClientRect()
        //console.log(xx,11)
        switch (type) {
            case 'RT':
                updateSize(id,offsetX)
                break;
            default:
                return
        }
    }

    render() {
        const { connectDragSource, x, y, background, id, current,width,height } = this.props;
        return connectDragSource(
            <div ref={e=>this.ref=e} onClick={(e) => this.onClickHandler(e, id)}
                className={`box-feature ${id === current ? 'z-cur' : ''}`}
                style={{ left: x, top: y, background,width,height }}
            >
                <div className='u-leftTop'></div>
                <div className='u-top'></div>
                <div className='u-rightTop'></div>
                <div className='u-right' onMouseDown={(e) => this.updateSize(e, 'RT')}></div>
                <div className='u-rightBottom'></div>
                <div className='u-bottom'></div>
                <div className='u-leftBottom'></div>
                <div className='u-left'></div>
            </div>
        )
    }
}
export default DragSource('ITEM', source, (connect, monitor) => ({
    connectDragSource: connect.dragSource(),
    isDragging: monitor.isDragging()
}))(BoxCmp)
