/*
 * @Author: yuanxy 
 * @Date: 2018-12-16 20:38:52 
 * @Last Modified by: yuanxy
 * @Last Modified time: 2018-12-16 23:41:58
 */
import React from 'react'
import {
    DragSource,

} from 'react-dnd'
import './itemBox.scss'

const source = {
    beginDrag(props) {
        return props
    },
    canDrag(props) {
        if (props.id !== props.current) {
            return false
        }
        return true
    }
}

class BoxCmp extends React.Component {
    state = {
        type: ''
    }
    componentDidMount() {
        this.mouseUp()
    }
    mouseUp = () => {
        document.addEventListener('mouseup', () => {
            document.removeEventListener('mousemove', this.computeSize);
        });
    };
    onClickHandler = (e, id) => {
        e.stopPropagation()
        this.props.selectItem(id)
    }


    updateSize = (e, type) => {
        e.preventDefault();
        e.stopPropagation();
        this.setState({
            type: type
        })
        document.addEventListener('mousemove', this.computeSize);
    }

    computeSize = (e) => {
        const { type } = this.state
        const { updateSize,id,width,height } = this.props
        const { offsetX, offsetY } = e
        console.log(width,offsetX,id)
        switch (type) {
            case 'RT':
                updateSize(id,Number(width+offsetX),Number(height+offsetY))
                break;
            default:
                return
        }
    }

    render() {
        const { connectDragSource, x, y, background, id, current } = this.props;
        return connectDragSource(
            <div onClick={(e) => this.onClickHandler(e, id)}
                className={`box-feature ${id === current ? 'z-cur' : ''}`}
                style={{ left: x, top: y, background }}
            >
                <div className='u-leftTop'></div>
                <div className='u-top'></div>
                <div className='u-rightTop'></div>
                <div className='u-right' onMouseDown={(e) => this.updateSize(e, 'RT')}></div>
                <div className='u-rightBottom'></div>
                <div className='u-bottom'></div>
                <div className='u-leftBottom'></div>
                <div className='u-left'></div>
            </div>
        )
    }
}
export default DragSource('ITEM', source, (connect, monitor) => ({
    connectDragSource: connect.dragSource(),
    isDragging: monitor.isDragging()
}))(BoxCmp)
