import React from 'react'
import {
    DragSource,
    DropTarget,
} from 'react-dnd'
import './itemBox.scss'

const source = {
    beginDrag(props) {
       return props
    },
    isDragging(props, monitor) {
        return props.id === monitor.getItem().id
    }
}


const target = {
    canDrop(props, monitor) {
        return true
    },

    drop(props, monitor, component) {

        const delta=monitor.getDifferenceFromInitialOffset()
        props.move(props.id,delta.x,delta.y)
    }
}


const TYPE = 'ITEM'

class BoxCmp extends React.Component{
    render() {
        const { connectDropTarget, connectDragSource,x,y } = this.props;
        return connectDragSource(
                connectDropTarget(
                    <div className='box-feature' style={{left:x,top:y}} />
                )
            )
    }
}
const Box = DropTarget(TYPE, target, (connect) => ({
    connectDropTarget: connect.dropTarget()
}))(DragSource(TYPE, source, (connect, monitor) => ({
    connectDragSource: connect.dragSource(),
    isDragging: monitor.isDragging()
}))(BoxCmp))

export default Box
