/*
 * @Author: zengjian 
 * @Date: 2018-12-10 19:22:03 
 * @Last Modified by: yuanxy
 * @Last Modified time: 2018-12-16 17:15:15
 */
let initialState = {
   components:[{
       id:0,
       x:0,
       y:0,
       width:100,
       height:100
   }]
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'move':
            return { ...state, x: action.payload.x, y: action.payload.y }
        default:
            return state
    }
}

export default reducer
